SELECT cor.CourseOfferId, c.CourseId, Year, Quartile, c.CourseName, CourseDescription, d.DegreeId, cor.ECTS, Dept, DegreeDescription, TotalECTS, t.TeacherId, TeacherName, Address, BirthyearTeacher, Gender FROM courseofferregistrations cor, courses c, degrees d, teachers t, teacherassignmentstocourses tatc WHERE cor.courseofferid = 1 and cor.courseid = c.courseid and d.degreeid = c.degreeid and t.teacherid = tatc.teacherid and tatc.courseofferid = cor.courseofferid LIMIT 1;
SELECT cor.courseofferid, cor.courseid, year, Quartile, s.studentid, studentname, address, birthyearstudent, gender, d.degreeid, dept, DegreeDescription, TotalECTS FROM studentregistrationstodegrees srd, studentassistants sa, courseofferregistrations cor, students s, degrees d WHERE sa.studentregistrationid = 3 and sa.courseofferid = cor.courseofferid and s.studentID = srd.studentid and d.degreeid = srd.degreeid and srd.studentregistrationid = sa.studentregistrationid LIMIT 1;
SELECT avg(Grade) FROM CourseOfferRegistrations WHERE StudentRegistrationId = 3;
